# Enable default encryption on bucket:
aws s3api put-bucket-encryption \  --bucket my-bucket \  --server-side-encryption-configuration '{
    "Rules": [{
      "ApplyServerSideEncryptionByDefault": {
        "SSEAlgorithm": "AES256"
      },
      "BucketKeyEnabled": true
    }]
  }'

# Create KMS key:
KEY_ID=$(aws kms create-key --description "S3 bucket encryption" --query 'KeyMetadata.KeyId' --output text)

# Enable KMS encryption:
aws s3api put-bucket-encryption \  --bucket my-bucket \  --server-side-encryption-configuration '{
    "Rules": [{
      "ApplyServerSideEncryptionByDefault": {
        "SSEAlgorithm": "aws:kms",
        "KMSMasterKeyID": "'$KEY_ID'"
      },
      "BucketKeyEnabled": true
    }]
  }'

# Enable encrypted-volumes rule:
aws configservice put-config-rule \  --config-rule '{
    "ConfigRuleName": "encrypted-volumes",
    "Source": {
      "Owner": "AWS",
      "SourceIdentifier": "ENCRYPTED_VOLUMES"
    }
  }'

# Enable S3 public read prohibited:
aws configservice put-config-rule \  --config-rule '{
    "ConfigRuleName": "s3-bucket-public-read-prohibited",
    "Source": {
      "Owner": "AWS",
      "SourceIdentifier": "S3_BUCKET_PUBLIC_READ_PROHIBITED"
    }
  }'

# Enable Security Hub:
aws securityhub enable-security-hub

# Enable AWS Foundational Security Best Practices:
aws securityhub batch-enable-standards \  --standards-subscription-requests '[{
    "StandardsArn": "arn:aws:securityhub:REGION::standards/aws-foundational-security-best-practices/v/1.0.0"
  }]'
